// Say we want to create 2 zone. The first one is a zone that Jaco can't go and the second is a zone that Jaco can access but with a lower speed.

// We declare the 2 zones
CZone zoneNoGo  = new CZone();
CZone zoneSlow; = new CZone();

// We declare the 4 points of the NoGo zone.
CJacoStructures.Point3d p1_NoGo = new CJacoStructures.Point3d();
CJacoStructures.Point3d p2_NoGo = new CJacoStructures.Point3d();
CJacoStructures.Point3d p3_NoGo = new CJacoStructures.Point3d();
CJacoStructures.Point3d p4_NoGo = new CJacoStructures.Point3d();

// We Declare 4 points for the Slow zone.
CJacoStructures.Point3d p1_Slow = new CJacoStructures.Point3d();
CJacoStructures.Point3d p2_Slow = new CJacoStructures.Point3d();
CJacoStructures.Point3d p3_Slow = new CJacoStructures.Point3d();
CJacoStructures.Point3d p4_Slow = new CJacoStructures.Point3d();

float SlowZoneLinearSpeed = 5.0f;
float SlowZoneAngularSpeed = 5.0f;
float Height = 40.0f;

...
// We set all the points.
...

// We create the 2 zones.
// On crer les 2 zones.
zoneNoGo = CZonesConfigurations.CreateBaseZoneXY(p1_NoGo, p2_NoGo, p3_NoGo, p4_NoGo, height, 0, 0);
zoneSlow = CZonesConfigurations.CreateBaseZoneXY(p1_slow, p2_Slow, p3_Slow, p4_Slow, height, SlowZoneLinearSpeed, SlowZoneAngularSpeed);

//Declaration and initialization of the object.
CClientConfigurations client = new CClientConfigurations();
            
//Here we set the value we need to configurate. Make sure that every value is valid with the CThreshold class.
client.AngleRetractedPosition = 40;
client.ClientName = "PseudoClient";
client.ClientNo = "3.14159";
client.ComplexRetractActive = 1;
client.DrinkingDistance = 0.04f;
client.DrinkingHeight = 0.03f;
client.Laterality = CJacoStructures.ArmLaterality.LeftHandedness;
client.MaxAngularSpeed = 0.5f;
client.MaxForce = 15;
             
//For example, the max linear speed cannot exceed the CThreshold.LINEAR_SPEED_MAX_UPPER value.
client.MaxLinearSpeed = 0.11f;
client.ModelNo = "666";
client.Organization = "S.C.R.S.";


